import { useState } from "react";
import Card from "./components/Card/Card";
import LogInCard from "./components/Card/LogInCard";
import Header from "./components/Layout/Header";
import Meals from "./components/Meals/Meals";
import CardProvider from "./store/CardProvider";

const App = () => {
  const [showCard, setShowCard] = useState(false);
  const [showLogCard, setShowLogCard] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const showCardOrderdHandler = () => {
    setShowCard(true);
  };

  const hideCardOrderdHandler = () => {
    setShowCard(false);
  };

  const showCardLogHandler = () => {
    setShowLogCard(true);
  };

  const hideCardLogHandler = () => {
    setShowLogCard(false);
  };

  const logedInHandler = () => {
    setIsLoggedIn(true);
  };

  return (
    <CardProvider>
      {!isLoggedIn && showLogCard ? (
        <LogInCard onHideCard={hideCardLogHandler} onLogIn={logedInHandler} />
      ) : (
        console.log(showLogCard)
      )}
      {showCard && <Card onHideCard={hideCardOrderdHandler} />}
      <Header
        onShowOrderCard={showCardOrderdHandler}
        onShowLogCard={showCardLogHandler}
        loggedState={isLoggedIn}
      />
      <main>
        <Meals />
      </main>
    </CardProvider>
  );
};

export default App;
