import { useState } from "react";
import Card from "./components/Card/Card";
import LogCard from "./components/Card/LogCard";
import Header from "./components/Layout/Header";
import Meals from "./components/Meals/Meals";
import CardProvider from "./store/CardProvider";

const App = () => {
  const [showCard, setShowCard] = useState(false);
  const [showLogCard, setShowLogCard] = useState(false);

  const showCardOrderdHandler = () => {
    setShowCard(true);
  };

  const hideCardOrderdHandler = () => {
    setShowCard(false);
  };

  const showCardLogHandler = () => {
    setShowLogCard(true);
  };

  const hideCardLogHandler = () => {
    setShowLogCard(false);
  };

  return (
    <CardProvider>
      {showLogCard && <LogCard onHideCard={hideCardLogHandler} />}
      {showCard && <Card onHideCard={hideCardOrderdHandler} />}
      <Header onShowCard={showCardOrderdHandler} />
      <main>
        <Meals />
      </main>
    </CardProvider>
  );
};

export default App;
