import { useContext } from "react";
import CardContext from "../../store/card-context";

import Modal from "../UI/Modal";
import styles from "./Card.module.css";

const Card = (props) => {
  const cardCtx = useContext(CardContext);

  const totalAmount = `$${cardCtx.totalAmount.toFixed(2)}`;
  const hasItems = cardCtx.items.length > 0;
  const cardItems = cardCtx.items.map((item) => <li>{item.name}</li>);

  return (
    <Modal onClose={props.onHideCard}>
      <>
        <ul className={styles["cart-items"]}>{cardItems}</ul>
        <div className={styles.total}>
          <span>Total</span>
          <span>{totalAmount}</span>
        </div>
        <div className={styles.actions}>
          <button className={styles["button--alt"]} onClick={props.onHideCard}>
            Close
          </button>
          {hasItems && <button className={styles.button}>Order</button>}
        </div>
      </>
    </Modal>
  );
};

export default Card;
