import ExpenseItem from "./ExpenseItem";
import Card from "../Ui/Card";
import ExpensesFilter from "./ExpensesFilter";
import "./Expenses.css";
import { useState } from "react";

const Expenses = (props) => {
  const [filteredYeard, setFilteredYear] = useState("2020");

  const filterChangeHandler = (selectedYear) => {
    setFilteredYear(selectedYear);
  };

  const filteredExpenses = props.items.filter((expense) => {
    return expense.date.getFullYear().toString() === filteredYeard;
  });

  let content =
    filteredExpenses.length === 0 ? (
      <p className="no-expense">no expenses found</p>
    ) : (
      filteredExpenses.map((oneItem) => (
        <ExpenseItem
          key={oneItem.id}
          title={oneItem.title}
          amount={oneItem.amount}
          date={oneItem.date}
        />
      ))
    );

  return (
    <Card className="expenses">
      <ExpensesFilter
        selected={filteredYeard}
        onChangeFilter={filterChangeHandler}
      />
      {content}
    </Card>
  );
};

export default Expenses;
