import { useContext } from "react";
import showCard from "../store/context";
import Modal from "../UI/Modal";
import styles from "./Card.module.css";

const Card = (props) => {
  const cardshown = useContext(showCard);
  cardshown.close = true;

  console.log(cardshown.close);

  const cardItems = [{ id: "c1", name: "Sushi", amount: 2, price: 12.99 }].map(
    (item) => <li>{item.name}</li>
  );

  return (
    <Modal>
      {cardItems.length > 0 ? (
        <>
          <ul className={styles["cart-items"]}>{cardItems}</ul>
          <div className={styles.total}>
            <span>Total Amount</span>
            <span>35.62</span>
          </div>
          <div className={styles.actions}>
            <button className={styles["button--alt"]}>Close</button>
            <button className={styles.button}>Order</button>
          </div>
        </>
      ) : (
        <p className={styles.noOrder}>No order yet</p>
      )}
    </Modal>
  );
};

export default Card;
